Scripts.OnClick:
local parent = self:GetParent();
local parentFunction = parent:GetScript("OnClick");
if ( parentFunction ) then
	parentFunction(parent, button, down);
end

Scripts.OnEnter:
if ( self.tooltip ) then
	GameTooltip:SetOwner(self, "ANCHOR_TOP");
	GameTooltip:SetText(self.tooltip, nil, nil, nil, nil, true);
end

Scripts.OnLeave:
if ( GameTooltip:GetOwner() == self ) then
	GameTooltip:Hide();
end

Scripts.OnMouseDown:
if ( self:IsEnabled() ) then
	self.TeamSizeText:SetPoint("LEFT", self, "LEFT", 12, -2);
	self.WinsLabel:SetPoint("TOP", self, "TOP", -11, -12);
	self.CurrentLabel:SetPoint("TOP", self, "TOP", 57, -12);
end

Scripts.OnMouseUp:
self.TeamSizeText:SetPoint("LEFT", self, "LEFT", 13, -1);
self.WinsLabel:SetPoint("TOP", self, "TOP", -10, -11);
self.CurrentLabel:SetPoint("TOP", self, "TOP", 58, -11);

Scripts.OnEnter:
if ( self.tooltip ) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltip, NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b, 1, true);
	GameTooltip:Show();
end

if ( self.tooltip ) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltip, NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b, 1, true);
end

Scripts.OnLoad:
										self.scrollBarHideable = true;
										ScrollFrame_OnLoad(self);
										ScrollFrame_OnScrollRangeChanged(self);
										local scrollBar = _G[self:GetName().."ScrollBar"];
										self.scrollBarBackground:SetParent(scrollBar);
										self.scrollBarArtTop:SetParent(scrollBar);
										self.scrollBarArtBottom:SetParent(scrollBar);
										scrollBar:SetPoint("TOPLEFT", self, "TOPRIGHT", 6, -18);
										scrollBar:SetPoint("BOTTOMLEFT", self, "BOTTOMRIGHT", 6, 15);
										self.defaultWidth = self:GetWidth();
										local resizeFunc = function(scrollBar)
											if ( scrollBar:IsShown() ) then
												self:SetWidth(self.defaultWidth);
											else
												self:SetWidth(self.defaultWidth + scrollBar:GetWidth() + 7);
											end
										end

										scrollBar:SetScript("OnHide", resizeFunc);
										scrollBar:SetScript("OnShow", resizeFunc);

										resizeFunc(scrollBar);
